    <script>
        const otherCategoryButton = document.getElementById('otherCategoryButton');
        const otherCategoryMenu = document.getElementById('otherCategoryMenu');
        const profileButton = document.getElementById('profileButton');
        const profileMenu = document.getElementById('profileMenu');

        const scrollHeader = document.getElementById('scrollHeader');
        const otherCategoryButtonScroll = document.getElementById('otherCategoryButtonScroll');
        const otherCategoryMenuScroll = document.getElementById('otherCategoryMenuScroll');
        const uploadButton = document.getElementById('uploadButton');
        const editProfileButton = document.getElementById('editProfileButton');
        const draftButton = document.getElementById('draftButton');
        const contentButton = document.getElementById('contentButton');
        const contentMenu = document.getElementById('contentMenu');

        otherCategoryButton.addEventListener('click', () => {
            otherCategoryMenu.classList.toggle('hidden');
        });

        profileButton.addEventListener('click', () => {
            profileMenu.classList.toggle('hidden');
            profileMenu.classList.toggle('scale-100');
        });

        otherCategoryButtonScroll.addEventListener('click', () => {
            otherCategoryMenuScroll.classList.toggle('hidden');
        });

        <?php if ($isPenulis): ?>
            draftButton.addEventListener('click', () => {
                location.href = '/authors/publishAuthor.php'; // Sesuaikan jalur jika perlu
            });

            uploadButton.addEventListener('click', () => {
                location.href = '/authors/Main_author.php'; // Arahkan ke halaman Main_author
            });
        <?php endif; ?>

        editProfileButton.addEventListener('click', () => {
            location.href = '/settings/mainSetting.php?page=umum'; // Pastikan jalur ini benar
        });

        let lastScrollTop = 0;
        window.addEventListener('scroll', () => {
            let scrollTop = window.scrollY || document.documentElement.scrollTop;
            if (scrollTop > lastScrollTop && scrollTop > 100) {
                // Scroll ke bawah
                scrollHeader.classList.remove('-translate-y-full');
                scrollHeader.classList.add('translate-y-0');
            } else {
                // Scroll ke atas
                scrollHeader.classList.remove('translate-y-0');
                scrollHeader.classList.add('-translate-y-full');
            }
            lastScrollTop = scrollTop <= 0 ? 0 : scrollTop; // For Mobile or negative scrolling
        });

        window.addEventListener('click', (e) => {
            if (!otherCategoryButton.contains(e.target) && !otherCategoryMenu.contains(e.target)) {
                otherCategoryMenu.classList.add('hidden');
            }
            if (!profileButton.contains(e.target) && !profileMenu.contains(e.target)) {
                profileMenu.classList.add('hidden');
                profileMenu.classList.remove('scale-100');
            }

            if (!otherCategoryButtonScroll.contains(e.target) && !otherCategoryMenuScroll.contains(e.target)) {
                otherCategoryMenuScroll.classList.add('hidden');
            }
            if (!draftButton.contains(e.target) && !draftMenu.contains(e.target)) {
                draftMenu.classList.add('hidden');
            }
            if (!contentButton.contains(e.target) && !contentMenu.contains(e.target)) {
                contentMenu.classList.add('hidden');
                contentButton.querySelector('.fa-chevron-down').classList.remove('rotate-180');
            }
        });

        // Fungsi untuk mengubah lokasi ke mainEditor.php
        function loadContent(url) {
            location.href = '/profile/' + url; // Pastikan '/profile/' adalah jalur yang benar
        }

        // Toggle notification menu
        document.getElementById("notificationButton").addEventListener("click", function(e) {
            const menu = document.getElementById("notificationMenu");
            const dot = document.getElementById("notificationDot");
            menu.classList.toggle("hidden");

            // Hilangkan titik merah jika menu dibuka
            if (!menu.classList.contains("hidden")) {
                dot.classList.add("hidden");
            }

            e.stopPropagation(); // Mencegah event bubbling
        });

        // Toggle dropdown menu di setiap notifikasi
        document.querySelectorAll(".notification-menu").forEach((button) => {
            button.addEventListener("click", function(e) {
                const dropdown = button.nextElementSibling;
                dropdown.classList.toggle("hidden");
                e.stopPropagation(); // Mencegah menutup menu utama
            });
        });

        // Hapus notifikasi
        document.querySelectorAll(".delete-notification").forEach((deleteButton) => {
            deleteButton.addEventListener("click", function(e) {
                const notification = deleteButton.closest("li");
                notification.remove();

                // Periksa apakah notifikasi kosong
                const notifications = document.getElementById("notifications");
                if (notifications.children.length === 0) {
                    document.getElementById("noNotifications").classList.remove("hidden");
                }

                // Pastikan dropdown notifikasi tetap terbuka
                const menu = document.getElementById("notificationMenu");
                menu.classList.remove("hidden");

                e.stopPropagation(); // Mencegah menutup menu utama
            });
        });


        // Tutup menu utama jika klik di luar
        document.addEventListener("click", function() {
            const menu = document.getElementById("notificationMenu");
            if (!menu.classList.contains("hidden")) {
                menu.classList.add("hidden");
            }
        });

        // Update current date
        function updateCurrentDate() {
            const currentDateElement = document.getElementById('currentDate');
            const now = new Date();
            const options = {
                weekday: 'long',
                year: 'numeric',
                month: 'long',
                day: 'numeric'
            };
            currentDateElement.textContent = now.toLocaleDateString('id-ID', options);
        }

        updateCurrentDate();

        document.addEventListener('DOMContentLoaded', () => {
            const userEmail = document.getElementById('userEmail');
            if (userEmail) {
                userEmail.addEventListener('click', () => {
                    loadContent('mainEditor.php');
                });
            }
        });

        // Fungsi untuk mengubah lokasi ke halaman profil
        function loadContent(url) {
            location.href = '/profile/' + url; // Pastikan '/profile/' adalah jalur yang benar
        }

        // Event listener untuk tombol konten
        contentButton.addEventListener('click', () => {
            contentMenu.classList.toggle('hidden');
            contentButton.querySelector('.fa-chevron-down').classList.toggle('rotate-180');
        });
        document.addEventListener('DOMContentLoaded', () => {
            const bacaNantiButton = document.getElementById('bacaNantiButton');
            const likedButton = document.getElementById('likedButton');

            if (bacaNantiButton) {
                bacaNantiButton.addEventListener('click', () => {
                    loadContent('bacaNanti.php');
                });
            }

            if (likedButton) {
                likedButton.addEventListener('click', () => {
                    loadContent('liked.php');
                });
            }
        });

        document.getElementById('searchForm').addEventListener('submit', function(event) {
            const searchInput = document.getElementById('searchInput').value.trim();
            if (!searchInput) {
                event.preventDefault(); // Mencegah pengiriman formulir
                toggleModal(true);
            }
        });

        document.getElementById('closeModalButton').addEventListener('click', function() {
            toggleModal(false);
        });

        function toggleModal(show) {
            const modal = document.getElementById('searchModal');
            if (show) {
                modal.classList.remove('hidden');
            } else {
                modal.classList.add('hidden');
            }
        }

        // Menutup modal saat klik di luar modal
        function closeModal(event) {
            if (event.target.id === 'searchModal') {
                toggleModal(false);
            }
        }
    </script>
